// src/pages/HomePage.js
import React from 'react';
import './HomePage.css';

function HomePage() {
    const topics = [
        { title: "EDR", subtitle: "Articles", image: "path_to_edr_image.jpg" },
        { title: "Network Security", subtitle: "Articles", image: "path_to_network_security_image.jpg" },
        { title: "IAM", subtitle: "Articles", image: "path_to_iam_image.jpg" },
        { title: "Comparisons", subtitle: "Articles", image: "path_to_comparisons_image.jpg" },
        { title: "Announcements", subtitle: "Articles", image: "path_to_announcements_image.jpg" }
    ];

    return (
        <div className="homepage">
            <div className="main-section">
                {/* Left Column: Text Content */}
                <div className="homepage-content">
                    <h1>
                        <span className="highlight loading-animation">..CIAM</span>
                    </h1>
                    <p>
                        Customer Identity and Access Management (CIAM) is a crucial cybersecurity framework that enables organizations to securely manage and protect customer identities, control access, and deliver personalized experiences.
                        {/* Add more content as needed */}
                    </p>
                    <button className="cta-button">Access to CIAM Details</button>
                </div>
                
                {/* Right Column: Image Content */}
                <div className="homepage-image">
                    <img src="path_to_robot_image.png" alt="Robot" className="robot-image" />
                    <img src="path_to_magazine_image.png" alt="Magazine Cover" className="magazine-image" />
                </div>
            </div>

            {/* Carousel Section */}
            <div className="hot-topics">
                <h2>Hot Topics</h2>
                <p>Don't miss out on the latest news and deep dives...</p>
                <div className="carousel">
                    <button className="carousel-btn left">←</button>
                    <div className="carousel-track">
                        {topics.map((topic, index) => (
                            <div className="carousel-card" key={index}>
                                <img src={topic.image} alt={topic.title} />
                                <h3>{topic.title}</h3>
                                <p>{topic.subtitle}</p>
                            </div>
                        ))}
                    </div>
                    <button className="carousel-btn right">→</button>
                </div>
            </div>
        </div>
    );
}

export default HomePage;
