// App.js
import React, { useEffect, useState } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';
import HomePage from './pages/HomePage';
import About from './pages/About';
import Spinner from './components/Spinner'; // 
import AdvancedAuthentication from './pages/AdvancedAuthentication';

function App() {
    const [loading, setLoading] = useState(true);
    const location = useLocation();

    useEffect(() => {
        setLoading(true); // Set loading to true on route change
        const timer = setTimeout(() => setLoading(false), 1000);
        return () => clearTimeout(timer);
    }, [location]);

    return (
        <div className="App">
            <Navbar />
            {loading ? (
                <Spinner />  // Use Spinner component here
            ) : (
                <Routes>
                    <Route path="/" element={<HomePage />} />
                    <Route path="/home" element={<HomePage />} />
                    <Route path="/about" element={<About />} />
                    <Route path="/advanced-authentication" element={<AdvancedAuthentication />} />
                </Routes>
            )}
        </div>
    );
}

export default App;
