import React, { useState, useEffect } from 'react';
import './HomePage.css';

function HomePage() {
    const [index, setIndex] = useState(0);
    const fullText = '..CIAM';

    useEffect(() => {
        const interval = setInterval(() => {
            setIndex((prevIndex) => {
                if (prevIndex < fullText.length) {
                    return prevIndex + 1;
                } else {
                    return 0; // Reset to 0 to loop the animation
                }
            });
        }, 200); // Adjust speed as needed

        return () => clearInterval(interval); // Cleanup interval on component unmount
    }, []);
    return (
        <div className="homepage">
            <div className="homepage-content">
                
            <h1><span className="highlight">{fullText.slice(0, index)}</span></h1>
                <p>
                Customer Identity and Access Management (CIAM) is a crucial cybersecurity framework 
                that enables organizations to securely manage and protect customer identities, control access, and deliver personalized experiences. Unlike traditional identity management systems, CIAM focuses on customer-facing applications, providing a seamless and secure experience for users. By incorporating features such as single sign-on (SSO), multifactor authentication (MFA), and secure data storage, CIAM helps organizations protect customer data against security threats while maintaining compliance with data privacy regulations. In todayâ€™s digital world, where data breaches and identity theft are constant risks, 
               .
                </p>
                <button className="cta-button">Access to CIAM Details</button>
            </div>
            <div className="homepage-image">
                <img src="path_to_robot_image.png" alt="Robot" className="robot-image" />
                <img src="path_to_magazine_image.png" alt="Magazine Cover" className="magazine-image" />
            </div>
        </div>
    );
}

export default HomePage;
