// App.js
import React, { useEffect, useState } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';
import HomePage from './pages/HomePage';
import Spinner from './components/Spinner';
import AdvancedAuthentication from './pages/AdvancedAuthentication';
import Footer from './components/Footer'; 
import OurCompany from './pages/OurCompany';
import { useMsal } from "@azure/msal-react";
import SinglesignOn from './pages/SinglesignOn';
import ProfilePage from './pages/ProfilePage';

function App() {
    const [loading, setLoading] = useState(true);
    const location = useLocation();
    const { instance } = useMsal();
    const [userDetails, setUserDetails] = useState(null);

    useEffect(() => {
        const initializeMSAL = async () => {
            try {
                const response = await instance.handleRedirectPromise(); // Handle redirect response
                if (response) {
                    console.log("Login successful:", response);
                    instance.setActiveAccount(response.account); // Set active account
                } else if (instance.getActiveAccount()) {
                    console.log("Active account found:", instance.getActiveAccount());
                } else {
                    console.log("No active account found. Redirecting to login.");
                    await instance.loginRedirect({ scopes: ["openid", "profile", "email"] });
                }
            } catch (error) {
                console.error("Error during MSAL initialization:", error);
            }
        };
    
        initializeMSAL();
    }, [instance]);

    return (
        <div className="App">
            <Navbar setUserDetails={setUserDetails} userDetails={userDetails} />
            {loading ? (
                <Spinner /> // Use Spinner component here
            ) : (
                <>
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        <Route path="/home" element={<HomePage />} />
                       <Route path="/advanced-authentication" element={<AdvancedAuthentication />} />
                       <Route path="/ourcompany" element={<OurCompany />} />
                       <Route path="/single-signon" element={<SinglesignOn />} />
                       <Route path="/profile" element={<ProfilePage user={userDetails} />} />
                    </Routes>
                    <Footer /> {/* Add Footer component here */}
                </>
            )}
        </div>
    );
}

export default App;
